<?phpnamespace library\webComponents;/*** textarea web component* @author cyril bazin* @package cu.core* @version 1.0*/class textarea extends \library\field{	public function build()	{		$widget = '';				$widget .= '<textarea class="form-control" name="'.$this->name.'"';				if(!empty($this->class))			$widget .= ' class="'.$this->class.'"';					if(!empty($this->readonly))			$widget .= ' readonly="'.$this->readonly.'"';					if(!empty($this->rows))			$widget .= ' rows="'.$this->rows.'"';							$widget .= '>';				if (!empty($this->value))			$widget .= ''.htmlspecialchars($this->value).'';				$widget .= ' </textarea>';							return $widget;	}}?>